import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import "../css/styles.css";
import Getdata from "./getdata";
import $ from "jquery";
// import "../css/dataTables.css";
import axios from "axios";
import Function from "./function";
import DataTables from "./dataTable";
import DataTable from "react-data-table-component";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";

class Layout_right extends Component {
    data=[];
    a=new Function();
    constructor(props) {
        super(props);
        this.state={
            data:[],
            name_product: "",
            categories: "null",
            price_product: "null",
            discount_price_product: "null",
            quantity_product: "null",
            mass_product: "null",
            ED: "null",
            MFG: "null",
            image: "null",
            company_name: "null",
            title: "null",
            category: [],
            company: []
          
        }
    }

    componentWillMount() {
        Getdata.get();
        
    }
    click=(jj)=>{
        $("#con").show();
        alert(jj);
    }
    createForm=()=>{
        const formData = new FormData();
        formData.append('name-product', this.state.name_product);
        formData.append('category', this.state.categories);
        formData.append('id', this.state.id);
        formData.append('price-product', this.state.price_product);
        formData.append('discount', this.state.discount_price_product);
        formData.append('quantity', this.state.quantity_product);
        formData.append('mass', this.state.mass_product);
        formData.append('ED', this.state.ED);
        formData.append('MFG', this.state.MFG);
        formData.append('title', this.state.title);
        formData.append('image-product', this.state.image);
        formData.append('company', this.state.company_name);
        
        return formData;
    }
    handleInputChange = (event) => {
        let value = event.target.value;

        let name = event.target.name;
        this.setState({
            [name]: value
        });
    }
    handleImage = (event) => {

        this.setState({
            image: event.target.files[0]
        })

    }
    delete=(id)=>{
        const formData = new FormData();
        formData.append('id', id);
        formData.append('method', "delete");
        
        this.a.Product(formData);
        setTimeout(()=>{
            if(JSON.parse(localStorage.getItem("code"))==0)
            alert("Dữ liệu bất đồng bộ!");
            else
            window.location.reload();
        }, 2000);
    }
    hideModal = () => {
        $("#myModal").hide();
        window.location.reload();
    };
    close=()=>{
        $("#con").hide();
        // alert(this.case);
    }
    handleSubmit = (event) => {
        event.preventDefault();
        const form = this.createForm();
        form.append("method", "POST");
        console.log(form);
        this.a.Product(form);
        setTimeout(()=>{
            if(JSON.parse(localStorage.getItem("code"))==0)
            alert("Dữ liệu bất đồng bộ!");
            else
            window.location.reload();
        }, 2000);
    }
    updateProduct=(event)=>{
        event.preventDefault();
        const form = this.createForm();
        form.append("method", "PUT");
        console.log(form);
        this.a.Product(form);
    }
    change=(id)=>{
        this.state.data.map(element=>{
            if(element.id==id){
                alert(element.id);
            }
        })
        $("#con").show();
    }
    componentDidMount() {
        axios({
            method: 'GET',
            data: { text: "jjjj" },
            url: 'http://localhost/react-project/product.php',
            //timeout: 4000, // 4 seconds timeout          
        })
            .then(response => {
                this.data=response.data;
                this.setState({ data: response.data });
                // console.log(response.data);
            })
            axios({
                method: 'GET',
                data: { text: "jjjj" },
                url: 'http://localhost/react-project/category.php',
                timeout: 4000, // 4 seconds timeout          
            })
                .then(response => {
                    this.setState({ category: response.data });
                    console.log(response);
                })
            axios({
                method: 'GET',
                data: { text: "jjjj" },
                url: 'http://localhost/react-project/company.php',
                timeout: 4000, // 4 seconds timeout          
            })
                .then(response => {
                    this.setState({ company: response.data });
                    console.log(response);
                })
            
    }
  
    render() {
        var table=null;
        if(this.state.data.length>0){
            table=<DataTables delete={this.delete} change={this.change} data={this.state.data}/>;
        }
        return (
            <Router>
            <div id="layoutSidenav_content">
                <img src="C:/images-react-project/15632021585d2c926e9e2961.83352159.jpg"></img>
                <main>
                    <div className="container-fluid">
                        <h1 className="mt-4">Dashboard</h1>
                        <ol className="breadcrumb mb-4">
                            <li className="breadcrumb-item active">Dashboard</li>
                        </ol>
                        <div className="row">
                            <div className="col-xl-3 col-md-6">
                                <div className="card bg-primary text-white mb-4">
                                    <div className="card-body">Product management</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between">
                                        <a className="small text-white stretched-link" href="Pages/admin_product.php">View Details <sup><b style={{color: "white"}}></b></sup></a>
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-xl-3 col-md-6">
                                <div className="card bg-warning text-white mb-4">
                                    <div className="card-body">Supply partner management</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between">
                                        <a className="small text-white stretched-link" href="Pages/admin_company.php">View Details<sup><b style={{color: "white"}}></b></sup></a>
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                           
                            <div className="col-xl-3 col-md-6">
                                <div className="card bg-danger text-white mb-4">
                                    <div className="card-body">Account admin management</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between">
                                        <a className="small text-white stretched-link" href="Pages/account_admin.php">View Details<sup><b style={{color: "white"}}></b></sup></a>
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-xl-3 col-md-6" >
                                <div className="card bg-danger text-white mb-4" style={{backgroundColor: "gray", border: "1px solid gray"}}>
                                    <div className="card-body" style={{backgroundColor: "gray"}}>Account user management</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between" style={{backgroundColor: "gray"}}>
                                        <a className="small text-white stretched-link" href="Pages/account_user.php">View Details<sup><b style={{color: "white"}}></b></sup></a>
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-xl-3 col-md-6">
                                <div className="card bg-danger text-white mb-4" style={{backgroundColor: "black", border: "1px solid black"}}>
                                    <div className="card-body" style={{backgroundColor: "black"}}>Order management</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between" style={{backgroundColor: "black"}}>
                                        <a className="small text-white stretched-link" href="Pages/order_manager.php">View Details<sup><b style={{color: "white"}}></b></sup></a>
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-xl-3 col-md-6">
                                <div className="card bg-danger text-white mb-4" style={{backgroundColor: "gray", border: "1px solid white"}}>
                                    <div className="card-body" style={{backgroundColor: "gray"}}>Shipping partners</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between" style={{backgroundColor: "gary"}}>
                                        <a className="small text-white stretched-link"><Link to="/about">View Details</Link><sup><b style={{color: "white"}}></b></sup></a>
                                        
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <button type="button" class="btn btn-primary btn-lg" data-toggle="modal" data-target="#myModal">Add product</button>
                        <div class="card mb-4">
                            <div class="card-header">
                                <i class="fas fa-table mr-1"></i>
                                DataTable product
                            </div>
                            <div class="card-body">
                                <div class="table-responsive">
                                    {table}
                                </div>
                            </div>
                        </div>

                        <div className="card mb-4">
                            <div className="card-header">
                                <i className="fas fa-table mr-1"></i>
                                DataTable Example
                            </div>
                            <div className="card-body">
                                <div className="table-responsive">
                                    
                                </div>
                            </div>
                        </div>
                    </div>
                </main>
                <footer className="py-4 bg-light mt-auto">
                    <div className="container-fluid">
                        <div className="d-flex align-items-center justify-content-between small">
                            <div className="text-muted">Copyright &copy; Your Website 2020</div>
                            <div>
                                <a href="#">Privacy Policy</a>
                                &middot;
                                <a href="#">Terms &amp; Conditions</a>
                            </div>
                        </div>
                    </div>
                </footer>
                <div class="modal" data-toggle="modal" id="con" tabindex="-1" role="dialog">
            
                <div class="modal-dialog" role="document">
                    <div class="modal-content">
                    <div class="modal-header">
                        <h5 class="modal-title">LOGIN</h5>
                        <button type="button" onClick={this.close} class="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <form onSubmit={this.handleSubmit}>
                    <div class="modal-body">
                        <label for=""> Email key</label><br />
                        <input type="email" name="email" onChange={this.handleInputChange} required /> <br/>
                        <label for=""> Password </label><br />
                        <input type="password" name="password" onChange={this.handleInputChange} required /> <br/>
                    </div>
                    <div class="modal-footer">
                        <button type="submit" class="btn btn-primary">Save changes</button>
                    </div>
                    </form>
                    </div>
                </div>
                </div>
                <div id="myModal" style={{ display: "none" }} className="modal">
                <div className="modal-content">
                    <button onClick={this.hideModal}> <h3>Đóng</h3> </button>
                    <form onSubmit={this.handleSubmit} id="form" enctype="multipart/form-data">
                        <div className="modal-header">
                            <h1> Thêm sản phẩm </h1></div>
                        <div className="modal-body">
                            <div className="container">
                                <div className="row">
                                    <div className="col">
                                        <label for=""> Nhập tên sản phẩm </label><br />
                                        <input type="text" name="name_product" onChange={this.handleInputChange} placeholder="Nhập tên sản phẩm" required /> <br />
                                        <label for=""> Danh mục sản phẩm </label><br />

                                        <select name="categories" id="industry" onChange={this.handleInputChange}>
                                            {
                                                this.state.category.map(element => {
                                                    return (<option value={element.id} > { element.industry} </option>)
                                                })
                                            }
                                        </select><br />
                                        <div className="in_price">
                                            <div>
                                                <label for=""> Giá nhập sản phẩm </label><br />
                                                <input type="number" min="1" name="price_product" onChange={this.handleInputChange} placeholder="Nhập giá sản phẩm" required />
                                            </div>
                                            <div>
                                                <label for=""> Giá bán </label><br />
                                                <input type="number" name="discount_price_product" onChange={this.handleInputChange} placeholder="Nhập giá bán" required />
                                            </div>
                                        </div>
                                        <label for=""> Số lượng sản phẩm </label><br />
                                        <input type="number" min="0" name="quantity_product" onChange={this.handleInputChange} placeholder="Nhập số lượng sản phẩm" required /> <br />
                                        <label for=""> Khối lượng sản phẩm </label><br />
                                        <input type="number" min="0" name="mass_product" onChange={this.handleInputChange} id="mass" placeholder="Nhập khối lượng sản phẩm" required /> <br />
                                        <label for=""> Ngày sản xuất </label> <input type="date" name="ED" id="" onChange={this.handleInputChange} required /> <br />
                                        <label for=""> Hạn sử dụng </label> <input type="date" name="MFG" id="" onChange={this.handleInputChange} required /> <br />
                                        <label for=""> Công ty sản xuất </label><br />
                                        <select name="company_name" id="company" onChange={this.handleInputChange}> {
                                            this.state.company.map(element => {
                                                return (<option value={element.id} > { element.name} </option>
                                                )
                                            })
                                        }
                                        </select>
                                        <br />
                                        <input type="file" name="image" onChange={this.handleImage} /><br />
                                        <span>

                                        </span><br />
                                        <label for=""> Mô tả sản phẩm </label><br />
                                        <textarea name="title" onChange={this.handleInputChange} id="" cols="30" rows="10" required> </textarea><br />
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="submit" style={{backgroundColor:"#007bff"}} id="add" value="add" className="btn btnprimary btn-lg" name="add">
                                Thêm Sản Phẩm </button> </div>
                    </form>
                </div>
            </div >
            <div id="myModal1" className="modal">
                <div className="modal-content">
                    <button onClick={this.hideModal}> <h3>Đóng</h3> </button>
                    <form onSubmit={this.handleSubmit} id="form" enctype="multipart/form-data">
                        <div className="modal-header">
                            <h1> Thêm sản phẩm </h1></div>
                        <div className="modal-body">
                            <div className="container">
                                <div className="row">
                                    <div className="col">
                                        <label for=""> Nhập tên sản phẩm </label><br />
                                        <input type="text" name="name_product" onChange={this.handleInputChange} placeholder="Nhập tên sản phẩm" required /> <br />
                                        <label for=""> Danh mục sản phẩm </label><br />

                                        <select name="categories" id="industry" onChange={this.handleInputChange}>
                                            {
                                                this.state.category.map(element => {
                                                    return (<option value={element.id} > { element.industry} </option>)
                                                })
                                            }
                                        </select><br />
                                        <div className="in_price">
                                            <div>
                                                <label for=""> Giá nhập sản phẩm </label><br />
                                                <input type="number" min="1" name="price_product" onChange={this.handleInputChange} placeholder="Nhập giá sản phẩm" required />
                                            </div>
                                            <div>
                                                <label for=""> Giá bán </label><br />
                                                <input type="number" name="discount_price_product" onChange={this.handleInputChange} placeholder="Nhập giá bán" required />
                                            </div>
                                        </div>
                                        <label for=""> Số lượng sản phẩm </label><br />
                                        <input type="number" min="0" name="quantity_product" onChange={this.handleInputChange} placeholder="Nhập số lượng sản phẩm" required /> <br />
                                        <label for=""> Khối lượng sản phẩm </label><br />
                                        <input type="number" min="0" name="mass_product" onChange={this.handleInputChange} id="mass" placeholder="Nhập khối lượng sản phẩm" required /> <br />
                                        <label for=""> Ngày sản xuất </label> <input type="date" name="ED" id="" onChange={this.handleInputChange} required /> <br />
                                        <label for=""> Hạn sử dụng </label> <input type="date" name="MFG" id="" onChange={this.handleInputChange} required /> <br />
                                        <label for=""> Công ty sản xuất </label><br />
                                        <select name="company_name" id="company" onChange={this.handleInputChange}> {
                                            this.state.company.map(element => {
                                                return (<option value={element.id} > { element.name} </option>
                                                )
                                            })
                                        }
                                        </select>
                                        <br />
                                        <input type="file" name="image" onChange={this.handleImage} /><br />
                                        <span>

                                        </span><br />
                                        <label for=""> Mô tả sản phẩm </label><br />
                                        <textarea name="title" onChange={this.handleInputChange} id="" cols="30" rows="10" required> </textarea><br />
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="submit" style={{backgroundColor:"#007bff"}} id="add" value="add" className="btn btnprimary btn-lg" name="add">
                                Thêm Sản Phẩm </button> </div>
                    </form>
                </div>
                
            
            </div >
            </div>
            </Router>
        );
    }
}


export default Layout_right;
