import React, { Component } from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from "axios";
class Testdata extends Component {
    constructor(props) {
        super(props);
        this.state={
            category:[],
            company:[]
        }
    }

    componentWillMount() {

    }

    componentDidMount() {
        axios({
            method: 'GET',
            data:{text:"jjjj"},
            url: 'http://localhost/react-project/category.php',
            timeout: 4000,    // 4 seconds timeout          
           })
           .then(response => {
             this.setState({category:response.data});
             console.log(response);
          })        
          .catch(error => console.error('timeout exceeded'));


          
          axios({
            method: 'GET',
            data:{text:"jjjj"},
            url: 'http://localhost/react-project/company.php',
            timeout: 4000,    // 4 seconds timeout          
           })
           .then(response => {
             this.setState({company:response.data});
             console.log(response);
          })        
          .catch(error => console.error('timeout exceeded'));
    }

    

    render() {
        return (
            <div>
                <button style={{marginTop:"50px"}} type="button" className="btn btn-primary btn-lg" data-toggle="modal" data-target="#myModal">Add product</button>
                <div id="myModal" style={{width:"70%"}} className="modal">
                    <div className="modal-content">
                        <form method="POST" id="form" enctype="multipart/form-data">
                            <div className="modal-header">
                                <h1>Thêm sản phẩm</h1>
                            </div>
                            <div className="modal-body">
                                <div className="container">
                                    <div className="row">
                                        <div className="col">
                                            <label for="">Nhập tên sản phẩm</label><br/>
                                            <input type="text" name="name-product" placeholder="Nhập tên sản phẩm" required/><br/>
                                            <label for="">Danh mục sản phẩm</label><br/>
                                            <select name="category" id="industry" onchange="">
                                            
                                            {this.state.category.map(element=>{
                                                    return(<option value={element.id}>{element.industry}</option>
                                                    )
                                                })}

                                                <option value="Other">Other</option>
                                            </select><br/>
                                            
                                            <div className="in_price">
                                                <div>
                                                    <label for="">Giá nhập sản phẩm</label><br/>
                                                    <input type="number" min="1" name="price-product" placeholder="Nhập giá sản phẩm"
                                                        required/>
                                                </div>
                                                <div>
                                                    <label for="">Giá bán</label><br/>
                                                    <input type="number" name="discount-price-product" placeholder="Nhập giá bán"
                                                        required/>
                                                </div>
                                            </div>
                                            <label for="">Số lượng sản phẩm</label><br/>
                                            <input type="number" min="0" name="quantity-product" placeholder="Nhập số lượng sản phẩm"
                                                required/><br/>
                                            <label for="">Khối lượng sản phẩm</label><br/>
                                            <input type="number" min="0" name="mass-product" id="mass"
                                                placeholder="Nhập khối lượng sản phẩm" required/><br/>
                                            <label for="">Ngày sản xuất</label>
                                            <input type="date" name="ED" id="" required/><br/>
                                            <label for="">Hạn sử dụng</label>
                                            <input type="date" name="MFG" id="" required/><br/>
                                            <label for="">Công ty sản xuất</label><br/>
                                            <select name="company" id="company" onchange="myFunction1()">
                                                {this.state.company.map(element=>{
                                                    return(<option value={element.id}>
                                                        {element.name}
                                                    </option>
                                                    )
                                                })}
                                                
                                                
                                                <option value="Other">Other</option>
                                            </select><br/>
                                            <div className="input-company" id="input-company">

                                            </div>

                                            <input type="file" name="image-product"/><br/>
                                            <span>
                                                
                                            </span><br/>
                                            <label for="">Mô tả sản phẩm</label><br/>
                                            <textarea name="title" id="" cols="30" rows="10" required></textarea><br/>
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button type="submit" id="add" value="add" className="btn btn-primary btn-lg" name="add">
                                    Thêm Sản Phẩm
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}


export default Testdata;
