import React, { Component } from 'react';
import PropTypes from 'prop-types';

class Modal_Data extends Component {
    constructor(props) {
        super(props);

    }

    componentWillMount() {

    }

    componentDidMount() {

    }
    
    render() {
        return (
            <div id="modal" className="modal">
                <div className="modal-content">
                    <button >
                        <h3>Đóng</h3>
                    </button>
                    <form  enctype="multipart/form-data">
                        <div className="modal-header">
                            <h1>  </h1>
                        </div>
                        <div className="modal-body">
                            <div className="container">
                                <div className="row">
                                    <div className="col">
                                        <label for=""> Mô tả sản phẩm </label><br />
                                        <textarea name="title" id="title" cols="30" rows="10"
                                            required></textarea><br />
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className="modal-footer">
                            <button type="submit" style={{backgroundColor:"#007bff"}} id="add" value="add"
                                className="btn btnprimary btn-lg" name="add">
                                Sửa Sản Phẩm </button>
                        </div>
                    </form>
                </div>
            </div>
        );
    }
}



export default Modal_Data;
