import React, { Component } from 'react';
import PropTypes from 'prop-types';

class Modal_Data extends Component {
    constructor(props) {
        super(props);

    }

    componentWillMount() {

    }

    componentDidMount() {

    }
    
    render() {
        return (
           
                                    <div>
                                        {()=>{
                                            if(this.props.typeIn="select"){
                                                return(<div>
                                                    <label for="">{this.props.title}</label><br />
                                                    <select name="categories" id="industry" onChange={this.props.change()}>
                                                        {
                                                        this.props.category.map(element => {
                                                        return (<option value={element.id}> { element.industry} </option>)
                                                        })
                                                        }
                                                    </select><br />
                                                </div>
                                                )
                                            }
                                            else if(this.props.typeIn="select"){
                                                return(<div>
                                                        <label for=""> Hình ảnh </label><br />
                                                        <input type="file" name="image" onChange={this.props.change()} /><br />
                                                    </div>
                                                )
                                            }
                                            else if(this.props.typeIn="textarea"){
                                                return(<div>
                                                    <label for=""> {this.props.title} </label><br />
                                                        <textarea name={this.props.name} id={this.props.id} onChange={this.props.change()} cols="30" rows="10"
                                                        required></textarea><br />
                                                </div>
                                            )
                                            }
                                            else
                                            return(<div>
                                                        <label for="">  </label><br />
                                                        <input type={this.props.typeIn} id={this.props.id} name={this.props.name}
                                                            onChange={this.props.change()} required />
                                                    </div>
                                            )
                                            
                                        }}
                                        <label for=""> Nhập tên sản phẩm </label><br />
                                        <input type="text" id="name" name="name_product" onChange={this.handleInputChange}
                                            placeholder="Nhập tên sản phẩm" required /> <br />
                                        <label for=""> Danh mục sản phẩm </label><br />

                                        
                                        <div className="in_price">
                                            <div>
                                                <label for=""> Giá nhập sản phẩm </label><br />
                                                <input type="number" min="1" id="price" name="price_product"
                                                    onChange={this.handleInputChange} placeholder="Nhập giá sản phẩm" required />
                                            </div>
                                            <div>
                                                <label for=""> Giá bán </label><br />
                                                <input type="number" id="discount" name="discount_price_product"
                                                    onChange={this.handleInputChange} placeholder="Nhập giá bán" required />
                                            </div>
                                        </div>
                                        <label for=""> Số lượng sản phẩm </label><br />
                                        <input type="number" min="0" id="quantity" name="quantity_product"
                                            onChange={this.handleInputChange} placeholder="Nhập số lượng sản phẩm" required />
                                        <br />
                                        <label for=""> Khối lượng sản phẩm </label><br />
                                        <input type="number" min="0" id="mass_product" name="mass" onChange={this.handleInputChange}
                                            id="mass" placeholder="Nhập khối lượng sản phẩm" required /> <br />
                                        <label for=""> Ngày sản xuất </label> <input type="date" name="ED" id="ED"
                                            onChange={this.handleInputChange} required /> <br />
                                        <label for=""> Hạn sử dụng </label> <input type="date" name="MFG" id="MFG"
                                            onChange={this.handleInputChange} required /> <br />
                                        <label for=""> Công ty sản xuất </label><br />
                                        <select name="company_name" id="company" onChange={this.handleInputChange}> {
                                            this.state.company.map(element => {
                                            return (<option value={element.id}> { element.name} </option>
                                            )
                                            })
                                            }
                                        </select>
                                        <br />
                                        
                                        <img src="" id="img" style={{width: "6rem" , height: "6rem" }} /><br />
                                        <span>

                                        </span><br />
                                        <label for=""> Mô tả sản phẩm </label><br />
                                        <textarea name="title" id="title" onChange={this.handleInputChange} cols="30" rows="10"
                                            required></textarea><br />
                                    </div>
        );
    }
}


export default Modal_Data;
