import React, { Component } from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from "axios";
class Testdata extends Component {
    constructor(props) {
        super(props);
        this.state = {

            name_product: "",
            categories: "null",
            price_product: "null",
            discount_price_product: "null",
            quantity_product: "null",
            mass_product: "null",
            ED: "null",
            MFG: "null",
            image:"null",
            company_name: "null",
            title: "null",
            name_industry:"null",
            nameCom:"null",
            address:"null",
            manager:"null",
            license:"null",
            phoneCom:"null",
            email:"null",

            category: [],
            company: []
        }
        this.handleInputChangeChange = this.handleInputChange.bind(this);
    }

    handleInputChange = (event) => {
        let value = event.target.value;
        
        let name = event.target.name;
        alert(name);
        if (name === "company_name") {
            if(value === "Other"){
            
            document.getElementById("input_company").innerHTML="<label for=''>Nhập tên công ty</label><br /><input type='text' name='nameCom' onChange={this.handleInputChange} placeholder='Nhập tên công ty' required /><br /><label for=''>Nhập địa chỉ công ty</label><br /><input type='text' name='address' onChange={this.handleInputChange} placeholder='Nhập địa chỉ công ty' required /><br /><label for=''>Giám đốc công ty</label><br /><input type='text' name='manager' onChange={this.handleInputChange} placeholder='Nhập giám đốc công ty' required /><br /><label for=''>Mã số thuế</label><br /><input type='text' name='license' onChange={this.handleInputChange} placeholder='Mã số thuế' required /><br /><label for=''>Phone</label><input type='phone' name='phoneCom' id='' onChange={this.handleInputChange} placeholder='Số điện thoại' required /><br /><label for=''>Email</label><br /><input type='email' name='email' onChange={this.handleInputChange} placeholder='Email' /><br />";
            }
            else
            document.getElementById("input_company").innerHTML = "";
        }
        
        if (name === "categories") {
            if(value === "Other")
            document.getElementById("in_industry").innerHTML = "<label for=''>Nhập ngành sản phẩm</label><br /><input type='text' name='name_industry' onChange={this.handleInputChange} placeholder='Nhập ngành sả phẩm' required /><br />";
            else
            document.getElementById("in_industry").innerHTML  = "";
        }
        
    

        this.setState({
            [name]: value
        });
    }
    handleImage=(event)=>{
        
        this.setState({
            image:event.target.files[0]
        })
        
    }
    handleSubmit = (event) => {

        event.preventDefault();
        console.log(this.state);
        const formData = new FormData();
        formData.append('name-product', this.state.name_product);
        if(this.state.name_industry!="null")
        formData.append('industry', this.state.name_industry);
        else
        formData.append('category', this.state.categories);

        formData.append('price-product', this.state.price_product);
        formData.append('discount', this.state.discount_price_product);
        formData.append('quantity', this.state.quantity_product);
        formData.append('mass', this.state.mass_product);
        formData.append('ED', this.state.ED);
        formData.append('MFG', this.state.MFG);
        formData.append('title', this.state.title);
        formData.append('image-product', this.state.image);


        if(this.state.nameCom!="Other"){
            formData.append('name-company', this.state.nameCom);
            formData.append('address', this.state.address);
            formData.append('manager', this.state.manager);
            formData.append('license', this.state.license);
            formData.append('phone', this.state.phoneCom);
            formData.append('email', this.state.email);
        }
        else
        formData.append('company', this.state.company_name);
        

        console.log(formData);
        let data1 = this.state;
        var headers = {
            'Content-Type': 'application/json'
          }
      
          axios.post('http://localhost/react-project/product.php', formData, headers)
          .then(function (response) {
             //handle success
             alert(response.data);
            console.log(response)
            console.log("success")
