            quantity_product: null,
            mass_product: null,
            ED: null,
            MFG: null,
            company_name: null,
            title: null,
            name_company:null,
            address_company:null,
            manager_company:null,
            license_number:null,
            image:null,
            phone:null,
            email:null,
            category: [],
            company: []
        }
        this.handleInputChangeChange = this.handleInputChange.bind(this);
    }

    handleInputChange = (event) => {
        let value = event.target.value;
        let name = event.target.name;

        if (name === "company_name" && value === "Other") {
            document.getElementById("input_company").style.display = "block";
        }
        else
            document.getElementById("input_company").style.display = "none";
        if (name === "categories" && value === "Other") {
            document.getElementById("in_industry").style.display = "block";
        }
        else
            document.getElementById("in_industry").style.display = "none";
    

        this.setState({
            [name]: value
        });
    }
    handleImage=(event)=>{
        
        this.setState=({
            image:event.target.file[0]
        })
    }
    handleSubmit = (event) => {

        e.preventDefault();
        const formData = new FormData();
        formData.append('image', this.state.image);

        let data1 = this.state;
        axios({
            method: 'POST',
            data: formData,
            url: 'http://localhost/react-project/product.php',
            timeout: 4000,    // 4 seconds timeout          
        })
            .then(response => {

                console.log(response);
            })
            .catch(error => console.error('timeout exceeded'));
        event.preventDefault();
    }
    componentWillMount() {

    }

    componentDidMount() {
        axios({
            method: 'GET',
            data: { text: "jjjj" },
            url: 'http://localhost/react-project/category.php',
            timeout: 4000,    // 4 seconds timeout          
        })
            .then(response => {
                this.setState({ category: response.data });
                console.log(response);
            })
            .catch(error => console.error('timeout exceeded'));



        axios({
            method: 'GET',
            data: { text: "jjjj" },
            url: 'http://localhost/react-project/company.php',
            timeout: 4000,    // 4 seconds timeout          
        })
            .then(response => {
                this.setState({ company: response.data });
                console.log(response);
            })
            .catch(error => console.error('timeout exceeded'));
    }



    render() {
        return (
            <div>
                <button style={{ marginTop: "50px" }} type="button" className="btn btn-primary btn-lg" data-toggle="modal" data-target="#myModal">Add product</button>
                <div id="myModal" style={{ display: "none" }} className="modal">
                    <div className="modal-content">

                        <form onSubmit={this.handleSubmit} id="form" enctype="multipart/form-data">
                            <div className="modal-header">
                                <h1>Thêm sản phẩm</h1>
                                helo{this.state.name_product}
                            </div>
                            <div className="modal-body">
                                <div className="container">
                                    <div className="row">
                                        <div className="col">
                                            <label for="">Nhập tên sản phẩm</label><br />
                                            <input type="text" name="name_product" onChange={this.handleInputChange} placeholder="Nhập tên sản phẩm" required /><br />
                                            <label for="">Danh mục sản phẩm</label><br />
                                            <select name="categories" id="industry" onChange={this.handleInputChange}>

                                                {this.state.category.map(element => {
                                                    return (<option value={element.id}>{element.industry}</option>
                                                    )
                                                })}

                                                <option value="Other">Other</option>
                                            </select><br />
                                            <div id="in_industry">
                                                <label for=''>Nhập ngành sản phẩm</label>
                                                <br />
                                                <input type='text' name='name-industry' placeholder='Nhập ngành sả phẩm' required />
                                                <br />
                                            </div>

                                            <div className="in_price">
                                                <div>
                                                    <label for="">Giá nhập sản phẩm</label><br />
                                                    <input type="number" min="1" name="price_product" onChange={this.handleInputChange} placeholder="Nhập giá sản phẩm"
                                                        required />
                                                </div>
                                                <div>
                                                    <label for="">Giá bán</label><br />
                                                    <input type="number" name="discount_price_product" onChange={this.handleInputChange} placeholder="Nhập giá bán"
                                                        required />
                                                </div>
                                            </div>
                                            <label for="">Số lượng sản phẩm</label><br />
                                            <input type="number" min="0" name="quantity_product" onChange={this.handleInputChange} placeholder="Nhập số lượng sản phẩm"
                                                required /><br />
                                            <label for="">Khối lượng sản phẩm</label><br />
                                            <input type="number" min="0" name="mass_product" onChange={this.handleInputChange} id="mass"
                                                placeholder="Nhập khối lượng sản phẩm" required /><br />
                                            <label for="">Ngày sản xuất</label>
                                            <input type="date" name="ED" id="" onChange={this.handleInputChange} required /><br />
                                            <label for="">Hạn sử dụng</label>
                                            <input type="date" name="MFG" id="" onChange={this.handleInputChange} required /><br />
                                            <label for="">Công ty sản xuất</label><br />
                                            <select name="company_name" id="company" onChange={this.handleInputChange} >
                                                {this.state.company.map(element => {
                                                    return (<option value={element.id}>
                                                        {element.name}
                                                    </option>
                                                    )
                                                })}


                                                <option value="Other">Other</option>
                                            </select><br />
                                            <div className="input_company" id="input_company">
                                                <label for=''>Nhập tên công ty</label>
                                                <br />
                                                <input type='text' name='name_company' placeholder='Nhập tên công ty' required />
                                                <br />
                                                <label for=''>Nhập địa chỉ công ty</label>
                                                <br />
                                                <input type='text' name='address_company' placeholder='Nhập địa chỉ công ty' required />
                                                <br />
                                                <label for=''>Giám đốc công ty</label>
                                                <br />
                                                <input type='text' name='manager_company' placeholder='Nhập giám đốc công ty' required />
                                                <br />
                                                <label for=''>Mã số thuế</label>
                                                <br />
                                                <input type='text' name='license_number' placeholder='Mã số thuế' required />
                                                <br />
                                                <label for=''>Phone</label>
                                                <input type='phone' name='phone' id='' placeholder='Số điện thoại' required />
                                                <br />
                                                <label for=''>Email</label>
                                                <br />
                                                <input type='email' name='email' placeholder='Email' />
                                                <br />
                                            </div>

                                            <input type="file" name="image_product" onChange={this.handleImage} /><br />
                                            <span>

                                            </span><br />
                                            <label for="">Mô tả sản phẩm</label><br />
                                            <textarea name="title" onChange={this.handleInputChange} id="" cols="30" rows="10" required></textarea><br />
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button type="submit" id="add" value="add" className="btn btnprimary btn-lg" name="add">
                                    Thêm Sản Phẩm
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}


export default Testdata;
