import React, { Component } from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from "axios";
class Testdata extends Component {
    constructor(props) {
        super(props);
        this.state = {

            name_product: "",
            categories: "null",
            price_product: "null",
            discount_price_product: "null",
            quantity_product: "null",
            mass_product: "null",
            ED: "null",
            MFG: "null",
            image:"null",
            company_name: "null",
            title: "null",
            name_industry1:"null",
            name_company:"null",
            address_company:"null",
            manager_company:"null",
            license_number:"null",
            phone:"null",
            email:"null",

            category: [],
            company: []
        }
        this.handleInputChangeChange = this.handleInputChange.bind(this);
    }

    handleInputChange = (event) => {
        let value = event.target.value;
        let name = event.target.name;

        if (name === "company_name") {
            if(value === "Other")
            document.getElementById("input_company").style.display = "block";
            else
            document.getElementById("input_company").style.display = "none";
        }
        
        if (name === "categories") {
            if(value === "Other")
            document.getElementById("in_industry").style.display = "block";
            else
            document.getElementById("in_industry").style.display = "none";
        }
        
    

        this.setState({
            [name]: value
        });
    }
    handleImage=(event)=>{
        
        this.setState({
            image:event.target.files[0]
        })
        
    }
    handleSubmit = (event) => {

        event.preventDefault();
        console.log(this.state.image);
        const formData = new FormData();
        formData.append('name-product', this.state.name_product);
        if(this.state.name_industry!="null")
        formData.append('industry', this.state.name_industry);
        else
        formData.append('category', this.state.categories);

        formData.append('price-product', this.state.price_product);
        formData.append('discount', this.state.discount_price_product);
        formData.append('quantity', this.state.quantity_product);
        formData.append('mass', this.state.mass_product);
        formData.append('ED', this.state.ED);
        formData.append('MFG', this.state.MFG);
        formData.append('title', this.state.title);
        formData.append('image-product', this.state.image);


        if(this.state.name_company!="null"){
            formData.append('name-company', this.state.name_company);
            formData.append('address-company', this.state.address_company);
            formData.append('manager', this.state.manager_company);
            formData.append('license', this.state.license_number);
            formData.append('phone', this.state.phone);
            formData.append('email', this.state.email);
        }
        else
        formData.append('company', this.state.company_name);
        

        console.log(formData);
        let data1 = this.state;
        var headers = {
            'Content-Type': 'application/json'
          }
      
          axios.post('http://localhost/react-project/product.php', formData, headers)
          .then(function (response) {
             //handle success
            console.log(response)
            console.log("success")
        })
        .catch(function (response) {
            //handle error
            console.log(response)
            console.log("sorry")
        });
        
    }
    componentWillMount() {

    }

    componentDidMount() {
        axios({
            method: 'GET',
            data: { text: "jjjj" },
            url: 'http://localhost/react-project/category.php',
            timeout: 4000,    // 4 seconds timeout          
        })
            .then(response => {
                this.setState({ category: response.data });
                console.log(response);
            })
            .catch(error => console.error('timeout exceeded'));



        axios({
            method: 'GET',
            data: { text: "jjjj" },
            url: 'http://localhost/react-project/company.php',
            timeout: 4000,    // 4 seconds timeout          
        })
            .then(response => {
                this.setState({ company: response.data });
                console.log(response);
            })
            .catch(error => console.error('timeout exceeded'));
    }



    render() {
        return (
            <div>
                <button style={{ marginTop: "50px" }} type="button" className="btn btn-primary btn-lg" data-toggle="modal" data-target="#myModal">Add product</button>
                <div id="myModal" style={{ display: "none" }} className="modal">
                    <div className="modal-content">

                        <form onSubmit={this.handleSubmit} id="form" enctype="multipart/form-data">
                            <div className="modal-header">
                                <h1>Thm sn phm</h1>
                                helo{this.state.name_product}
                            </div>
                            <div className="modal-body">
                                <div className="container">
                                    <div className="row">
                                        <div className="col">
                                            <label for="">Nhp tn sn phm</label><br />
                                            <input type="text" name="name_product" onChange={this.handleInputChange} placeholder="Nhp tn sn phm" required /><br />
                                            <label for="">Danh mc sn phm</label><br />
                                            
                                            <select name="categories" id="industry" onChange={this.handleInputChange}>

                                                {this.state.category.map(element => {
                                                    return (<option value={element.id}>{element.industry}</option>
                                                    )
                                                })}

                                                <option value="Other">Other</option>
                                            </select><br />
                                            <div id="in_industry">
                                                <label for=''>Nhp ngnh sn phm</label>
                                                <br />
                                                <input type='text' name='name-industry' onChange={this.handleInputChange} placeholder='Nhp ngnh s phm' required />
                                                <br />
                                            </div>

                                            <div className="in_price">
                                                <div>
                                                    <label for="">Gi nhp sn phm</label><br />
                                                    <input type="number" min="1" name="price_product" onChange={this.handleInputChange} placeholder="Nhp gi sn phm"
                                                        required />
                                                </div>
                                                <div>
                                                    <label for="">Gi bn</label><br />
                                                    <input type="number" name="discount_price_product" onChange={this.handleInputChange} placeholder="Nhp gi bn"
                                                        required />
                                                </div>
                                            </div>
                                            <label for="">S lng sn phm</label><br />
                                            <input type="number" min="0" name="quantity_product" onChange={this.handleInputChange} placeholder="Nhp s lng sn phm"
                                                required /><br />
                                            <label for="">Khi lng sn phm</label><br />
                                            <input type="number" min="0" name="mass_product" onChange={this.handleInputChange} id="mass"
                                                placeholder="Nhp khi lng sn phm" required /><br />
                                            <label for="">Ngy sn xut</label>
                                            <input type="date" name="ED" id="" onChange={this.handleInputChange} required /><br />
                                            <label for="">Hn s dng</label>
                                            <input type="date" name="MFG" id="" onChange={this.handleInputChange} required /><br />
                                            <label for="">Cng ty sn xut</label><br />
                                            <select name="company_name" id="company" onChange={this.handleInputChange} >
                                                {this.state.company.map(element => {
                                                    return (<option value={element.id}>
                                                        {element.name}
                                                    </option>
                                                    )
                                                })}


                                                <option value="Other">Other</option>
                                            </select><br />
                                            <div className="input_company" id="input_company">
                                                <label for=''>Nhp tn cng ty</label>
                                                <br />
                                                <input type='text' name='name_company' placeholder='Nhp tn cng ty' required />
                                                <br />
                                                <label for=''>Nhp a ch cng ty</label>
                                                <br />
                                                <input type='text' name='address_company' placeholder='Nhp a ch cng ty' required />
                                                <br />
                                                <label for=''>Gim c cng ty</label>
                                                <br />
                                                <input type='text' name='manager_company' placeholder='Nhp gim c cng ty' required />
                                                <br />
                                                <label for=''>M s thu</label>
                                                <br />
                                                <input type='text' name='license_number' placeholder='M s thu' required />
                                                <br />
                                                <label for=''>Phone</label>
                                                <input type='phone' name='phone' id='' placeholder='S in thoi' required />
                                                <br />
                                                <label for=''>Email</label>
                                                <br />
                                                <input type='email' name='email' placeholder='Email' />
                                                <br />
                                            </div>

                                            <input type="file" name="image" onChange={this.handleImage} /><br />
                                            <span>

                                            </span><br />
                                            <label for="">M t sn phm</label><br />
                                            <textarea name="title" onChange={this.handleInputChange} id="" cols="30" rows="10" required></textarea><br />
                                        </div>
                                    </div>
                                </div>
                            </div>
                            <div className="modal-footer">
                                <button type="submit" id="add" value="add" className="btn btnprimary btn-lg" name="add">
                                    Thm Sn Phm
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            </div>
        );
    }
}


export default Testdata;
