import React, { Component } from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';
import "../css/styles.css";
import { useHistory } from "react-router-dom";
// import "../css/dataTables.css";
import axios from "axios";
import DataTable from "react-data-table-component";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
class Layout_right extends Component {
    // history = useHistory();
    constructor(props) {
        super(props);
        this.state={
            data:[],
          
        }
    }

    componentWillMount() {

    }

    componentDidMount() {
        axios({
            method: 'GET',
            data: { text: "jjjj" },
            url: 'http://localhost/react-project/product.php',
            //timeout: 4000, // 4 seconds timeout          
        })
            .then(response => {
                this.setState({ data: response.data });
            })
            .catch(error => console.error('timeout exceeded'));
        
    }
  
    
    render() {
        const data=this.state.data;
        data.map(element=>{
            this.state.data.map(gg=>{
                element.image=<img src={gg.image}></img>
            })
            
           
        })
        console.log(this.state.data);

        const columns = [
            {
              name: "Name",
              selector: "name",
              sortable: true
            },
            {
              name: "Price",
              selector: "price",
              sortable: true
            },
            {
              name: "Sell price",
              selector: "sell_price",
              sortable: true,
              right: true
            },
            {
                name: "Mass",
                selector: "mass",
                sortable: true,
                right: true
              },
              {
                name: "Quantity",
                selector: "quantity",
                sortable: true,
                right: true
              },
              {
                name: "Title",
                selector: "title",
                sortable: true,
                right: true
              },
              {
                name: "Category",
                selector: "industry",
                sortable: true,
                right: true
              },
              {
                name: "ED",
                selector: "ED",
                sortable: true,
                right: true
              },
