import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import "../css/styles.css";
import Getdata from "./getdata";
import $ from "jquery";
// import "../css/dataTables.css";
import axios from "axios";
import DataTables from "./dataTable";
import DataTable from "react-data-table-component";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";

class Layout_right extends Component {
    // history = useHistory();
    data=[];
    case="l";
    constructor(props) {
        super(props);
        this.state={
            data:[],
          
        }
    }

    componentWillMount() {
        Getdata.get();
        
    }
click=()=>{
    $("#con").show();
    alert(this.case);
}
    componentDidMount() {

        axios({
            method: 'GET',
            data: { text: "jjjj" },
            url: 'http://localhost/react-project/product.php',
            //timeout: 4000, // 4 seconds timeout          
        })
            .then(response => {
                this.data=response.data;
                this.setState({ data: response.data });
                // console.log(response.data);
            })
            .catch(error => console.error('timeout exceeded'));
            
    }
  
    render() {
        let img=[];

        var table=null;
        if(this.state.data.length>0){
            
            table=<DataTables change={()=>this.click()} data={this.state.data}/>;
        }
        
        // var a="image";
        this.state.data.map(e=>{
            console.log(Object.values(e));
        })
        if(this.state.data[0]!=null)
        Object.keys(this.state.data[0]).map(r=>{
            console.log(r);
        });
        const columns = [
            {
              name: "Name",
              selector: "name",
              sortable: true
            },
            {
              name: "Price",
              selector: "price",
              sortable: true
            },
            {
              name: "Sell price",
              selector: "sell_price",
              sortable: true,
              right: true
            },
            {
                name: "Mass",
                selector: "mass",
                sortable: true,
                right: true
              },
              {
                name: "Quantity",
                selector: "quantity",
                sortable: true,
                right: true
              },
              {
                name: "Title",
                selector: "title",
                sortable: true,
                right: true
              },
              {
                name: "Category",
                selector: "industry",
                sortable: true,
                right: true
              },
              {
                name: "ED",
                selector: "ED",
                sortable: true,
                right: true
              },
              {
                name: "MFG",
                selector: "MFG",
                sortable: true,
                right: true
              },
              {
                name: "Company",
                selector: "company",
                sortable: true,
                right: true
              },
              {
                name: "Image",
                selector: "image",
                sortable: true,
                right: true
              }
          ];
        return (
            <Router>
            <div id="layoutSidenav_content">
                <main>
                    
                    <div className="container-fluid">
                        <h1 className="mt-4">Dashboard</h1>
                        <ol className="breadcrumb mb-4">
                            <li className="breadcrumb-item active">Dashboard</li>
                        </ol>
                        <button type="button" class="btn btn-primary btn-lg" data-toggle="modal" data-target="#myModal">Add product</button>
                        <div className="row">
                            <div className="col-xl-3 col-md-6">
                                <div className="card bg-primary text-white mb-4">
                                    <div className="card-body">Product management</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between">
                                        <a className="small text-white stretched-link" href="Pages/admin_product.php">View Details <sup><b style={{color: "white"}}></b></sup></a>
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-xl-3 col-md-6">
                                <div className="card bg-warning text-white mb-4">
                                    <div className="card-body">Supply partner management</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between">
                                        <a className="small text-white stretched-link" href="Pages/admin_company.php">View Details<sup><b style={{color: "white"}}></b></sup></a>
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                           
                            <div className="col-xl-3 col-md-6">
                                <div className="card bg-danger text-white mb-4">
                                    <div className="card-body">Account admin management</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between">
                                        <a className="small text-white stretched-link" href="Pages/account_admin.php">View Details<sup><b style={{color: "white"}}></b></sup></a>
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-xl-3 col-md-6" >
                                <div className="card bg-danger text-white mb-4" style={{backgroundColor: "gray", border: "1px solid gray"}}>
                                    <div className="card-body" style={{backgroundColor: "gray"}}>Account user management</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between" style={{backgroundColor: "gray"}}>
                                        <a className="small text-white stretched-link" href="Pages/account_user.php">View Details<sup><b style={{color: "white"}}></b></sup></a>
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-xl-3 col-md-6">
                                <div className="card bg-danger text-white mb-4" style={{backgroundColor: "black", border: "1px solid black"}}>
                                    <div className="card-body" style={{backgroundColor: "black"}}>Order management</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between" style={{backgroundColor: "black"}}>
                                        <a className="small text-white stretched-link" href="Pages/order_manager.php">View Details<sup><b style={{color: "white"}}></b></sup></a>
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-xl-3 col-md-6">
                                <div className="card bg-danger text-white mb-4" style={{backgroundColor: "gray", border: "1px solid white"}}>
                                    <div className="card-body" style={{backgroundColor: "gray"}}>Shipping partners</div>
                                    <div className="card-footer d-flex align-items-center justify-content-between" style={{backgroundColor: "gary"}}>
                                        <a className="small text-white stretched-link"><Link to="/about">View Details</Link><sup><b style={{color: "white"}}></b></sup></a>
                                        
                                        <div className="small text-white"><i className="fas fa-angle-right"></i></div>
                                    </div>
                                </div>
                            </div>
                           
                        </div>
                        <div class="card mb-4">
                            <div class="card-header">
                                <i class="fas fa-table mr-1"></i>
                                DataTable product
                            </div>
                            <div class="card-body">
                                <div class="table-responsive">
                                
                                    <table class="table table-bordered" id="dataTable" width="100%" cellspacing="0">
                                    <thead>
                                            <tr>
                                                <th>Name</th>
                                                <th>Price</th>
                                                <th>Discount</th>
                                                <th>Mass</th>
                                                <th>Quantity</th>
                                                <th>Title</th>
                                                <th>Image</th>
                                                <th>Category</th>
                                                <th>ED</th>
                                                <th>MFG</th>
                                                <th>Company</th>
                                                <th></th>
                                            </tr>
                                        </thead>
                                        <tfoot>
                                            <tr>
                                                <th>Name</th>
                                                <th>Price</th>
                                                <th>Discount</th>
                                                <th>Mass</th>
                                                <th>Quantity</th>
                                                <th>Title</th>
                                                <th>Image</th>
                                                <th>Category</th>
                                                <th>ED</th>
                                                <th>MFG</th>
                                                <th>Company</th>
                                                <th></th>
                                            </tr>
                                        </tfoot>
                                        <tbody>
                                            {this.state.data.map(element=>{
                                                return(<tr>
                                                    <td>
                                                        {element.name}
                                                        
                                                    </td>
                                                    <td>
                                                        {element.price}
                                                    </td>
                                                    <td>
                                                        {element.sell_price}
                                                    </td>
                                                    <td>
                                                        {element.mass}
                                                    </td>
                                                    <td>
                                                        {element.quantity}
                                                        <button style={{float: "right"}}>+</button>
                                                    </td>
                                                    <td>
                                                        {element.title}
                                                    </td>
                                                    <td>
                                                    <img src={element.image} class="img-responsive" style={{width:"4rem", height:"5rem"}}/>
                                                    </td>
                                                    <td>
                                                        {element.industry}
                                                    </td>
                                                    <td>
                                                        {element.ED}
                                                    </td>
                                                    <td>
                                                        {element.MFG}
                                                    </td>
                                                    <td>
                                                        {element.company}
                                                    </td>
                                                    <td>
                                                        <button name="add" onClick={()=>this.click(element.id)}><img src="https://taiwebs.com/upload/icons/systemmodeler.png" style={{width:"2rem"}}></img></button>
                                                        
                                                        <button type="submit" name="delete"><img src="https://cdn3.iconfinder.com/data/icons/social-messaging-ui-color-line/254000/82-512.png" style={{width: "2rem", height: "2rem"}} alt=""/></button>
                                                    </td>
                                                </tr>
                                                    )
                                            })}
                                        </tbody>
                                        </table>
                                        {table}
                                </div>
                            </div>
                        </div>

                        <div className="card mb-4">
                            <div className="card-header">
                                <i className="fas fa-table mr-1"></i>
                                DataTable Example
                            </div>
                            <div className="card-body">
                                <div className="table-responsive">
                                    
                                </div>
                            </div>
                        </div>
                    </div>
                </main>
                <footer className="py-4 bg-light mt-auto">
                    <div className="container-fluid">
                        <div className="d-flex align-items-center justify-content-between small">
                            <div className="text-muted">Copyright &copy; Your Website 2020</div>
                            <div>
                                <a href="#">Privacy Policy</a>
                                &middot;
                                <a href="#">Terms &amp; Conditions</a>
                            </div>
                        </div>
                    </div>
                </footer>
                <div class="modal" id="con" tabindex="-1" role="dialog">
            
                <div class="modal-dialog" role="document">
                    <div class="modal-content">
                    <div class="modal-header">
                        <h5 class="modal-title">LOGIN</h5>
                        <button type="button" onClick={this.hide} class="close" data-dismiss="modal" aria-label="Close">
                        <span aria-hidden="true">&times;</span>
                        </button>
                    </div>
                    <form onSubmit={this.handleSubmit}>
                    <div class="modal-body">
                        <label for=""> Email </label><br />
                        <input type="email" name="email" onChange={this.handleInputChange} required /> <br/>
                        <label for=""> Password </label><br />
                        <input type="password" name="password" onChange={this.handleInputChange} required /> <br/>
                    </div>
                    <div class="modal-footer">
                        <button type="submit" class="btn btn-primary">Save changes</button>
                    </div>
                    </form>
                    </div>
                </div>
                </div>
            </div>
            </Router>
        );
    }
}


export default Layout_right;
